/* --------------------------------------------------------------------------------------------------------- */

$main: #FCF9EE;
$sub: #222222;
$english: 'Bookk Myungjo';
$border: (1px solid #222);

@mixin size($width, $height){
  width: $width;
  height: $height;
}

@mixin text($height) {
  text-align: center;
  line-height: $height;
}
/* -------------------------------------------------------------------------------------------------------------- */
body{
  font-family: 'KOTRA_GOTHIC';
  @include size(100%, auto );
  background-color: $main;
}


/* header */

.fixed{
  position: fixed;
  top: 0; left: 0;
}
header{
  position:absolute;
  top: 0;
  left: 0;
  width: 100%;
  transition: top 0.2s ease-in-out;
}
.rolling_ex{
  @include size(100%, 40px);
  background-color: $sub;

  .rolling{
    @include size(100%, 40px);
    background-color: $sub;
    color: #fff;
    overflow: hidden;
    display: flex;
    position: relative;

    .list{
      line-height: 40px;
      display: flex;
      
      li{
        font-size: 14px;
        padding-right: 20px;
        width: 1000px;
      }/* li */
    }/* .list */
    p{
      color: #fff;
      position: absolute;
      right: 10px;
      top: 5px;
      font-weight: 900;
      cursor: pointer;
      z-index: 1;
    }
  }/* .rolling */
}/* rolling_ex */

@keyframes flowRolling {
  0% {transform: translateX(0);}
  100% {transform: translateX(-100%);}
}
/* 상단 flowrolling */


.header_ex{
  @include size(100%, auto);
  position: relative;
  border-bottom: 1px solid #fff;
  box-sizing: border-box;
  .header{
    @include size(1300px, 80px);
