    .menu{
      float: left;
      margin-left: 45px;
      font-family: $english;
      color: white;
      font-size: 20px;
      .main_menu{
        
        .item{
          float: left;
          margin-top: 38px;
          height: 100px;
          width: 100px;

          .item__contents{
            width: 100%;
            position: absolute;
            padding-bottom: 10px;
            margin-top: 16px;
            display: none;
            z-index: 9;
            background-color: $main;
            left: 0;

            .contents__menu{
              background-color: $main;
              width: 800px;
              height: 50px;
              color: $sub;
              position: relative;
              margin: 0 auto;
              
              .inner{
                display: flex;
                width: 100%;
               
                li{
                  float: left;
                  margin-right: 30px;
                  font-size: 14px;
                  line-height: 50px;
                }
              }
            }
            .submenu01{
              padding-left: 32px;
            }
            .submenu02{
              padding-left: 240px;
            }
          }
          .submenu03{
            padding-left: 220px;
          }
          .submenu04{
            padding-left: 320px;
          }
        }
      }
    }

    .main_menu .item:hover .item__name{
      color: $sub;
      border-radius: 6px 6px 0 0;
      display: inline-block;
      position: relative;
    }
    .main_menu .item .item__name::after{
      content: '';
      width: 0%;
      left: 50%;
      height: 1px;
      display: block;
      position: absolute;
      margin-top: 1px;
      border-bottom: 1px solid #222;
      transition:all 0s ease;
    }
    .main_menu .item:hover .item__name::after{
      content: '';
      width: 120%;
